function e(e,t="datetime"){if(!e)return"-";try{const i=new Date(e);if(isNaN(i.getTime()))return console.warn("유효하지 않은 날짜:",e),"-";switch(t){case"date":return i.toLocaleDateString("ko-KR",{timeZone:"Asia/Seoul",year:"numeric",month:"2-digit",day:"2-digit"});case"time":return i.toLocaleTimeString("ko-KR",{timeZone:"Asia/Seoul",hour:"2-digit",minute:"2-digit",second:"2-digit"});case"datetime":return i.toLocaleString("ko-KR",{timeZone:"Asia/Seoul",year:"numeric",month:"2-digit",day:"2-digit",hour:"2-digit",minute:"2-digit"});case"relative":return function(e){const t=(new Date).getTime()-e.getTime(),i=Math.floor(t/6e4),o=Math.floor(t/36e5),r=Math.floor(t/864e5);return i<1?"방금 전":i<60?`${i}분 전`:o<24?`${o}시간 전`:r<7?`${r}일 전`:e.toLocaleDateString("ko-KR",{timeZone:"Asia/Seoul",month:"short",day:"numeric"})}(i);default:return i.toLocaleString("ko-KR",{timeZone:"Asia/Seoul"})}}catch(i){return console.error("시간 변환 오류:",i),"-"}}export{e as f};
